import { useRouteLoaderData, redirect, defer } from 'react-router-dom';
import EventItem from '../components/EventItem';
import EventsList from '../components/EventsList';

function EventDetailPage() {
    const data = useRouteLoaderData('event');

    return (
        <>
            <EventItem event={data.event} />
            <EventsList />
        </>
    )

}

export default EventDetailPage;

async function loadEvent(id) {
    const response = await fetch('http://localhost:8080/events/' + id);

    if (!response.ok) {
        throw new Response(JSON.stringify({ message: 'Could not fetch details for selected event.' }), { status: 500 });
    } else {
        const resData = await response.json();
        return resData.event;
    }
}

async function loadEvents() {
    const response = await fetch('http://localhost:8080/events');

    if (!response.ok) {
        throw new Response("Could not fetch events", { status: 500 });
    } else {
        const resData = await response.json();
        return resData.events;
    }
}

export async function loader({ request, params }) {
    const id = params.eventID;
    return defer({
        event: loadEvent(id),
        events: loadEvents()
    });
};

export async function action({ request, params }) {
    const eventID = params.eventID;
    const response = await fetch('http://localhost:8080/events/' + eventID, { method: request.method });

    if (!response.ok) {
        throw new Response(JSON.stringify({ message: 'Could not remove selected event' }), { status: 500 })
    }

    return redirect('/events');
}
